	for (int i=0;i<g[v].size();i++)
	{
		int id=g[v][i];
		int to=get_other(id,v);
		if (dead[id])
			continue;
		if (used[to])
			continue;
		used[to]=used[v]+1;
		par[to]=id;
		dfs(to);
	}
}

