inline void dfs(int u = 1, int par = 0) {
	unsigned int s1 = 0, s2 = 0, &s = dp[u];
	for (int i = head[u]; i; i = nxt[i]) {
		int &v = to[i];
		if(v != par) {
			dfs(v, u);
			s2 |= (dp[v] & s1);
			s1 |= dp[v];
		}
	}
	s = 1;
	while(s <= s2) {
		s <<= 1;
	}
	while(s & s1) {
		s <<= 1;
	}
	s = ((s > 1) ? (s1 & ~(s - 1)) : s1) | s;
	return;
}

