const int N = 1e5+5, p = 1e9+7, bit = 60;
struct edge{ int to, nxt; } e[N<<1];
int head[N], cnt = 0, deg[N];
inline void add(int x,int y) {
	deg[x]++, deg[y]++; 
	e[++cnt].to = y, e[cnt].nxt = head[x], head[x] = cnt;
	e[++cnt].to = x, e[cnt].nxt = head[y], head[y] = cnt;
