inline void dfs(int x,int fa=0) { 
	ez(i,x) if( to != fa ) dfs(to,x);
	memset(g,0,sizeof(g));
	ez(i,x) if( to != fa ) { 
		rep(j,0,bit) if( (f[to] >> j) & 1 ) g[j]++;
	}
	int ret = 0; 
	dow(i,bit,0) if( g[i] >= 2 ) { ret = i + 1; break; }
	rep(i,ret,bit) if( !g[i] ) { ret = i; break; }
	f[x] = 1ll << ret;
	rep(i,ret+1,bit) if( g[i] ) f[x] |= 1ll << i;
}

