constexpr int MAX = 20;

class DUninity {
public:
    int N;
    vector<vector<int>> E;

    int dfs(int u, int p) {
        int any = 0, two = 0;
        for (int v: E[u]) if (v != p) {
            int s = dfs(v, u);
            two |= any & s;
            any |= s;
        }
        int ans = MAX;
        for (int i = MAX-1; i >= 0; --i) {
            if (two&1<<i) break;
            if (any&1<<i) {} else ans = i;
        }
        int cur = 1<<ans|(any&~((1<<ans)-1));
        return cur;
    }

    void solve(istream& cin, ostream& cout) {
        cin >> N;
        E.resize(N);
        for (int i = 0; i < N - 1; ++i) {
            int a, b; cin >> a >> b;
            --a; --b;
            E[a].push_back(b);
            E[b].push_back(a);
        }

        cout << logceil(dfs(0, -1)) - 1 << '\n';
    }
};


int main() {
	ios_base::sync_with_stdio(false); cin.tie(nullptr); cout.tie(nullptr);
	DUninity solver;
	std::istream& in(std::cin);
	std::ostream& out(std::cout);
	solver.solve(in, out);
    return 0;
}

