  int mn = 0;
  for (int i=LOG-1; i>=0; i--)
    if (nesmije[node][i] > 1) {
      mn = i + 1;
      break;
    }

  FOR(i, mn, LOG) {
    if (!nesmije[node][i]) {
      p[node] = i;
      //      TRACE(node _ p[node]);
      break;
    }
  }

  assert(p[node] != -1);
  rje = max(rje, p[node]);

  if (par != -1) {
    nesmije[par][p[node]]++;
    FOR(i, p[node], LOG)
      if (nesmije[node][i])
	nesmije[par][i]++;
  }
}

