	for (int p = l[u]; p >= 0; p = E[p].x) {
		int v = E[p].v;
		if (v != f) dfs(v, u);
	}
	for (int i = 0; i <= 20; i++) cnt[i] = 0;
	for (int p = l[u]; p >= 0; p = E[p].x) {
		int v = E[p].v;
		if (v != f) {
			for (int j = 0; j <= 20; j++) {
				if (dp[v] & (1<<j)) {
					++ cnt[j];
				}
			}
		}
	}
	int mn = 0;
	for (int i = 0; i <= 20; i++) if (cnt[i] >= 2) mn = i+1;
	int val = 0;
	for (int i = mn; i <= 20; i++) {
		if (!cnt[i]) {
			val = i;
			break;
		}
	}
	dp[u] ^= (1 << val);
	for (int i = val; i <= 20; i++) {
		if (cnt[i]) {
			dp[u] ^= (1<<i);
		}
	}
}

