const int N=int(1e5)+5;

int n;

struct node;
struct edge{
	node *ed;
	edge *next;
} E[N<<1],*newE=E;
struct node{
	edge *son;
	node *pa;
	int level;
	int size;
	bool done;
	void dfs(int lev,node *from=NULL){
		size=1;
		for(edge *e=son;e;e=e->next) if(!e->ed->done&&e->ed!=from){
			e->ed->dfs(lev,this);
			size+=e->ed->size;
		}
	}
} V[N];

node* getroot(node *u){
	while(true){
		node *v=NULL;
		for(edge *e=u->son;e;e=e->next) if(!e->ed->done&&e->ed->size*2>u->size){
			v=e->ed;
			break;
		}
		if(!v) break;
		int t=u->size;
		u->size-=v->size;
		v->size=t;
		u=v;
	}
	return u;
}

int ans=0;

void dianfen(node *u,node *fa=NULL,int lev=0){
	ans=max(ans,lev);
	u->dfs(lev);
	u=getroot(u);
	u->pa=fa;
	u->level=lev;
	u->dfs(lev);
	u->done=true;
	for(edge *e=u->son;e;e=e->next) if(!e->ed->done){
		node *v=e->ed;
		dianfen(v,u,lev+1);
	}
}

int main(){
	scanf("%d",&n);
	for(int i=1;i<n;i++){
		int x,y; scanf("%d%d",&x,&y);
		*newE=(edge){V+y,V[x].son},V[x].son=newE++;
		*newE=(edge){V+x,V[y].son},V[y].son=newE++;
	}
	dianfen(&V[1]);
	printf("%d\n",ans);
}

