using namespace std;

const int N = 1e5 + 500;
const int K = 30;

vector < int > v[N];

int obr[N], doc[N][K], par[N], ja[N], sol, n, deg[N];
queue < int > Q;

void ide(int x){
	for(int y : v[x]){
		if(!obr[y]){
			par[x] = y;
			v[x].erase(find(v[x].begin(), v[x].end(), y));
			break;
		}
	}
	for(int y : v[x]){
		for(int k = 0;k < K;k++)
			doc[x][k] += doc[y][k];
	}
	int mini = K - 1;
	for(;mini >= 0 && doc[x][mini] < 2;mini--);
	ja[x] = mini + 1;
	for(;doc[x][ja[x]];ja[x]++);
	for(int k = 0;k <= ja[x];k++)
		doc[x][k] = 0;
	//printf("x = %d ja = %d par = %d\n", x, ja[x], par[x]);
	doc[x][ja[x]] = 1; obr[x] = 1;
	sol = max(sol, ja[x]);
}

int main(){
	scanf("%d", &n);
	for(int i = 1;i < n;i++){
		int x, y; scanf("%d%d", &x, &y);
		v[x].PB(y); v[y].PB(x);
		deg[x]++, deg[y]++;
	}
	for(int i = 1;i <= n;i++){
		if(deg[i] == 1)
			Q.push(i);
	}
	for(int i = 1;i <= n;i++){
		int gas = Q.front(); Q.pop();
		ide(gas); deg[par[gas]]--;
		if(deg[par[gas]] <= 1)
			Q.push(par[gas]);
	}
	printf("%d\n", sol);
	return 0;
}

#include <bits/stdc++.h>

const int MAXN = 100010;
int head[MAXN], nxt[MAXN << 1], to[MAXN << 1], tot;
void addedge(int b, int e) {
	nxt[++tot] = head[b]; to[head[b] = tot] = e;
	nxt[++tot] = head[e]; to[head[e] = tot] = b;
}
int n, ans;
int dfs(int u, int fa = 0) {
	int pre = 0, hav = 0;
	for (int i = head[u]; i; i = nxt[i]) if (to[i] != fa) {
		int t = dfs(to[i], u);
		hav |= pre & t;
		pre |= t;
	}
	int p = std::max(__builtin_ctz(pre + 1), hav ? std::__lg(hav) + 1 : 0);
	ans = std::max(ans, p);
	pre ^= pre & (1 << p) - 1;
	return pre | 1 << p;
}
