}

#include <bits/stdc++.h>
using namespace std;

typedef pair<int, int> pii;
const int N = 1e5 + 100, inF = N;

int n, u, v, ans = inF, down[N], up[N];
bool mark[N];
vector<int> nei[N];

void find_down(int v, vector<int> *vec, int par = -1) {
	(*vec).push_back(v);
	for (int u: nei[v])
		if(u != par && mark[u] == false) {
			find_down(u, vec, v);
			down[v] = max(down[v], down[u] + 1);
		}
}

void find_up(int v, int par = -1) {
	pii mx1 = pii(up[v] + 1, v), mx2 = pii(-inF, -inF);
	for (int u: nei[v])
		if(u != par && mark[u] == false) {
			mx2 = max(mx2, pii(down[u] + 2, u));
			if(mx2 > mx1)
				swap(mx2, mx1);
		}
	for (int u: nei[v])
		if(u != par && mark[u] == false) {
			if(u != mx1.second)
				up[u] = mx1.first;
			else
				up[u] = mx2.first;
			find_up(u, v);
		}
}

int solve(int v) {
	vector<int> vec;
	find_down(v, &vec);
	find_up(v);
	int mini = N, cen = v;
	for (int u: vec)
		if(max(up[u], down[u]) < mini) {
			mini = max(up[u], down[u]);
			cen = u;
		}
	for (int u: vec)
		up[u] = down[u] = 0;
	mark[cen] = true;
	int maxi = -1;
	for (int u: nei[cen])
		if(mark[u] == false)
			maxi = max(maxi, solve(u));
	return maxi + 1;
}

int main() {
	ios::sync_with_stdio(false), cin.tie(0);
	cin >> n;
	for (int i = 0; i < n - 1; i++) {
		cin >> u >> v;
		nei[--u].push_back(--v);
		nei[v].push_back(u);
	}
	cout << solve(0);
	return 0;
}

#include <bits/stdc++.h>
using namespace std;

const int EXP = 30;
const int DIM = 200005;

vector<int> edg[DIM];

int dfs(int x, int f, int &ans)
{
    vector<int> aux(EXP, 0);

    for (int y : edg[x]) if (y != f) {
        int val = dfs(y, x, ans);

        for (int i = 0; i < EXP; ++i)
            aux[i] += ((val >> i) & 1);
    }

    int pos = -1;
    for (int i = 0; i < EXP; ++i)
        if (aux[i] >= 2) pos = i;

    for (int i = pos + 1; i < EXP; ++i) if (!aux[i]) {
        int msk = (1 << i);
        for (int j = i + 1; j < EXP; ++j)
            msk ^= (1 << j) * aux[j];

        ans = max(ans, i);
        return msk;
    }

    return 0;
}


int main(void)
{
