vector<int> g[N];
bool used[N];
int par[N];
int len[N];
int m_len[N];
vector<int> g1[N];
vector<int> order;

void get_len(int u, int p)
{
    order.push_back(u);
    len[u] = 0;
    for (int v : g[u])
        if (!used[v] && v != p)
        {
            get_len(v, u);
            len[u] = max(len[u], len[v] + 1);
        }
}

void dfs(int u, int p, int l)
{
    m_len[u] = max(len[u], l);
    int pos = 0;
    vector<int> pref, suff;
    pref.push_back(0);
    for (int v : g[u])
        if (!used[v] && v != p)
        {
            pref.push_back(len[v] + 1);
            suff.push_back(len[v] + 1);
        }
    suff.push_back(0);
    for (int v : g[u])
        if (!used[v] && v != p)
        {
            dfs(v, u, max(l, max(pref[pos], suff[pos + 1])) + 1);
            pos++;
        }
}

queue<pair<int, int> > q;

void solve()
{
    while (!q.empty())
    {
        auto p = q.front();
        q.pop();
        order.clear();
        get_len(p.fi, -1);
        dfs(p.fi, -1, 0);
        int x = -1;
        for (int u : order)
            if (x == -1 || m_len[u] < m_len[x])
                x = u;
        par[x] = p.se;
        used[x] = true;
        for (int v : g[x])
            if (!used[v])
                q.push({v, x});
    }
}

int dfs1(int u)
{
    int res = 0;
    for (int v : g1[u])
        res = max(res, dfs1(v) + 1);
    return res;
}

