using namespace std;

bool hasBit(int mask, int bit) {
	return !!(mask & (1 << bit));
}

vector<int> G[500000];

int DFS(int node, int par) {

	vector<int> Cnt(31);
	for(auto vec : G[node]) {
		if(vec == par) continue;

		int take = DFS(vec, node);
		for(int bit = 0; bit <= 30; ++bit)
			Cnt[bit] += hasBit(take, bit);
	}

	int tag;
	for(tag = 30; tag >= 0; --tag)
		if(Cnt[tag] >= 2)
			break;
	for(++tag; Cnt[tag] >= 1; ++tag);

	int ans = (1 << tag);
	for(++tag; tag <= 30; ++tag)
		ans |= (Cnt[tag] << tag);
	return ans;
}

