#include <bits/stdc++.h>

using namespace std;
typedef long long ll;
typedef long double ld;
const int maxn = 1e5 + 10;

int n;

int sz[maxn];

bool visited[maxn], mark[maxn];

int ans[maxn];

vector <int> t[maxn];

int dfs (int u, int par = -1) {
	sz[u] = 1;
	for (auto w : t[u]) {
		if (w != par and !mark[w]) {
			sz[u] += dfs (w, u);
		}
	}
	return sz[u];
}

void centroidDecomposition (int x, int now) {
	int Max = dfs (x), par = -1;
	while (true) {
		bool found = 0;
		for (auto w : t[x]) {
			if (par != w and !mark[w] and sz[w] > Max / 2) {
				par = x;
				x = w;
				found = 1;
				break;
			}
		}
		if (!found)
			break;
	}
	mark[x] = 1;
	ans[x] = now;
	for (auto w : t[x]) {
		if (!mark[w]) {
			centroidDecomposition (w, now + 1);
		}
	}
}

void addEdge (int u, int w) {
	t[u].push_back(w);
	t[w].push_back(u);
}

int main () {
	ios_base::sync_with_stdio (false);
	cin >> n;
	for (int i = 1; i <= n - 1; i++) {
		int u, w;
		cin >> u >> w;
		addEdge (u, w);
	}
	centroidDecomposition (1, 0);
	cout << *max_element(ans + 1, ans + n + 1) << endl;
	return 0;
}

#include <cstdio>
