using namespace std;

struct Edge {
  int t,next;
  Edge() {}
  Edge(int a,int b):t(a),next(b) {}
}; 

Edge e[200005];
int head[100005];

int f[100005],g[100005],ans;

void dfs(int x,int fa) {
  int sum=0;
  for(int i=head[x];i;i=e[i].next)
    if (e[i].t!=fa) {
    	int u=e[i].t;
    	dfs(u,x);
    	sum|=(g[x]&g[u]);
    	g[x]|=g[u];
	}
  for(int i=0;i<30;i++)
    if (!(sum&(1<<i))&&!(g[x]&(1<<i))) {
    	f[x]=i;
    	break;
	} 
  g[x]=((g[x]>>f[x]<<f[x])|(1<<f[x]));
  ans=max(ans,f[x]);
}

int main() {
  int n;
  scanf("%d",&n);
  for(int i=1;i<n;i++) {
  	int x,y;
  	scanf("%d%d",&x,&y);
  	e[2*i-1]=Edge(y,head[x]);
  	head[x]=2*i-1;
  	e[2*i]=Edge(x,head[y]);
  	head[y]=2*i;
  }
  dfs(1,0);
  printf("%d\n",ans);
  return 0;
}

