#define NMAX 100005
#define LMAX 21

int answer, n, mask[NMAX], viz[NMAX];
vector<int> tree[NMAX];

void dfs(int node){
    viz[node] = 1;

    int lim = tree[node].size(), f[LMAX];
    memset(f, 0, sizeof(f));

    for(int i = 0; i < lim; i++){
        int neigh = tree[node][i];
        if(!viz[neigh]){
            dfs(neigh);
            for(int j = 0; j < LMAX; j++)
                if(mask[neigh] & (1 << j))
                    f[j]++;
        }
    }

    int lastComm = -1;
    for(int i = LMAX - 1; i >= 0; i--){
        if(f[i] >= 2){
            lastComm = i;
            break;
        }
    }

    int takenMex = 0;
    for(int i = lastComm + 1; i < LMAX; i++){
        if(f[i] && takenMex)
            mask[node] += (1 << i);
        else if(!f[i] && !takenMex){
            answer = max(answer, i);
            mask[node] += (1 << i);
            takenMex = 1;
        }
    }
}

int main (){
    int a, b;

    scanf("%d",&n);
    for(int i = 1; i < n; i++){
        scanf("%d%d",&a,&b);
        tree[a].push_back(b);
        tree[b].push_back(a);
    }

    dfs(1);

    printf("%d\n", answer);

    return 0;
}

