int tmp = 1234;
int zzz = 0;

int cd(int v, bool root = false) {
    csz(v, -1);
    zzz += sz[v];
    if (zzz > 100000) {
        zzz = 0;
        if (clock() > 1.9 * CLOCKS_PER_SEC) {
            cout << tmp << endl;
            exit(0);
        }
    }
    int par = -1;
    int szv = sz[v];
    while (true) {
        bool done = true;
        for (int to : g[v]) if (to != par && !del[to] && 2 * sz[to] >= szv) {
            par = v;
            v = to;
            done = false;
            break;
        }
        if (done) break;
    }
    int res, worst, wcnt;
    par = -1;
    bool firstTime = true;
    do {
        if (firstTime) {
            firstTime = false;
        } else {
            del[v] = false;
            par = v;
            v = worst;
        }
        del[v] = true;
        res = 0;
        worst = -1;
        wcnt = 0;
        if (par != -1) {
            res = cd(par) + 1;
            worst = par;
            wcnt = 1;
        }
        for (int to : g[v]) if (!del[to] && to != par) {
            int val = cd(to) + 1;
            if (val > res) {
                res = val;
                worst = to;
                wcnt = 1;
            } else if (val == res) {
                ++wcnt;
            }
        }
        if (root) {
            tmp = min(tmp, res);
        }
    } while (wcnt == 1 && worst != par);
    del[v] = false;
    return res;
}

