#include <vector>
#include <iostream>
#include <algorithm>
using namespace std;
int solve(int pos, int pre, vector<vector<int> > &g) {
	int req = 0, mx = 0;
	for (int i : g[pos]) {
		if (i == pre) continue;
		int res = solve(i, pos, g);
		int res_req = 0;
		while ((res >> res_req) & 1) ++res_req;
		req = max(req, res_req);
		mx = max(mx, res);
	}
	++mx;
	while (req != 0 && (mx & (1 << (req - 1)))) ++mx;
	return mx;
}
int main() {
	cin.tie(0);
	ios_base::sync_with_stdio(false);
	int n, a, b;
	cin >> n;
	vector<vector<int> > g(n);
	for (int i = 0; i < n - 1; ++i) {
		cin >> a >> b; --a, --b;
		g[a].push_back(b);
		g[b].push_back(a);
	}
	int ret = solve(0, -1, g);
	int ans = 0;
	while (2 << ans < ret) ++ans;
	cout << ans << '\n';
	return 0;
}

