using namespace std;

int main() {
	// input
	int n; scanf("%d", &n);
	vector<vector<int> > g(n);
	repeat (i, n - 1) {
		int a, b; scanf("%d%d", &a, &b); -- a; -- b;
		g[a].push_back(b);
		g[b].push_back(a);
	}
	// solve
	function<int (int, int)> go = [&](int i, int parent) {
		int cnt1 = 0, cnt2 = 0;
		for (int j : g[i]) if (j != parent) {
			int q = go(j, i);
			cnt2 |= cnt1 & q;
			cnt1 |= q;
		}
		int p = 1;
		while (p <= cnt2 or cnt1 & p) p <<= 1;
		return (cnt1 & ~ (p - 1)) | p;
	};
	int p = go(0, -1);
	int k = 31 - __builtin_clz(p);
	// output
	printf("%d\n", k);
	return 0;
}

