#define repm(i,a,b) for(int i = (int)(a) ; i > (int)(b) ; --i)
#define repv(t,it,v) for(vector<t>::iterator it = v.begin() ; it != v.end() ; ++it)

int N;
vector<int> V[100010];

int lg(int a){
	int b = -1;
	for(; a > 0 ; a /= 2) ++b;
	return b;
}

int dfs(int p , int q){
	int x = 0;
	int y = 0;
	repv(int,it,V[q]){
		if(p == (*it)) continue;
		int z = dfs(q,(*it));
		y |= (x&z);
		x |= z;
	}
	int w = lg(y) + 1;
	x >>= w;
	while(x % 2 == 1){
		x /= 2;
		++w;
	}
	return ((x + 1) << w);
}

