        g[a].push_back(b);
        g[b].push_back(a);
    }
    // solve
    int d = diameter(g);
    int k = 0;
    for (int acc = 1; acc < d + 1; ) {
        acc = 2 * acc + 1;
        ++ k;
    }
    // output
    printf("%d\n", k);
    return 0;
}

