const int MAX_N = 100005;

class Edge {
public:
  int nxt, to;
} e[MAX_N << 1];

int head[MAX_N], cnt;
void addedge(int u, int v) {
  e[++cnt] = (Edge){head[u], v}, head[u] = cnt;
  e[++cnt] = (Edge){head[v], u}, head[v] = cnt;
}

int N, f[MAX_N];

void dfs(int u, int v) {
  for (int i = head[u]; i; i = e[i].nxt)
    if (e[i].to != v)
      dfs(e[i].to, u);
  int vis = 0, twice = 0;
  for (int i = head[u]; i; i = e[i].nxt) 
    if (e[i].to != v) {
      twice |= vis & f[e[i].to];
      vis |= f[e[i].to];
    }
  if (!twice) {
    f[u] = vis + 1;
  } else {
    int k = 20;
    while (!(twice >> k & 1)) k--;
    f[u] = (vis ^ (vis & ((1 << k + 1) - 1))) + (1 << k + 1);
  }
}

