const int N = 1e5 + 1;
vint t[N];
int siz[N];
bitset<N> added;
int calcSize(int cur, int p) {
    siz[cur] = 1;
    for (int i : t[cur])
        if (!added.test(i) && i != p)
            siz[cur] += calcSize(i, cur);
    return siz[cur];
}

int make_centroid(int cur, int h) {
    int p = calcSize(cur, -1);
    int lim = p / 2;
    if (p == 1)
        return h;
    bool again = 1;
    int pr = -1;
    int opt = inf;
    while (again) {
        again = 0;
        for (int i : t[cur])
            if (i != pr && !added.test(i) && siz[i] > lim) {
                pr = cur;
                cur = i;
                again = 1;
                break;
            } else if (i != pr && !added.test(i) && p % 2 == 0 && siz[i] == lim) {
                added.set(i);
                opt = -1;
                for (int j : t[i])
                    if (!added.test(j))
                        opt = max(opt, make_centroid(j, h + 1));
                added.reset(i);
            }
    }

    int man = 0;
    added.set(cur);
    for (int i : t[cur])
        if (!added.test(i))
            man = max(man, make_centroid(i, h + 1));
    added.reset(cur);
    return min(man, opt);
}

