const int N = 1e5 + 5;
int n, dp[N][2], L[N], R[N];
vector<int> adj[N];

void dfs1(int u, int par) {
	for (auto v: adj[u])
		if (v ^ par) {
			dfs1(v, u);
			dp[u][0] = max(dp[u][0], dp[v][0] + 1);
		}
}

void dfs2(int u, int par) {
	for (int i = 0; i <= adj[u].size(); i++)
		L[i] = R[i] = dp[u][1];
	for (int i = 0; i < adj[u].size(); i++)
		L[i + 1] = max(L[i], adj[u][i] ^ par? dp[adj[u][i]][0] + 1: 0);
	for (int i = adj[u].size() - 1; i; i--)
		R[i - 1] = max(R[i], adj[u][i] ^ par? dp[adj[u][i]][0] + 1: 0);
	for (int i = 0; i < adj[u].size(); i++)
		if (adj[u][i] ^ par)
			dp[adj[u][i]][1] = max(L[i], R[i]) + 1;
	for (auto v: adj[u])
		if (v ^ par)
			dfs2(v, u);
}

