const int N = 1e5 + 3;

int subsize[N];
bool visited[N];
int treesize;
void dfs(int u, int par)
{
	subsize[u] = 1;
	for(int i = 0; i < adj[u].size(); i++)
	{
		int v = adj[u][i];
		if(visited[v]) continue;
		if(v == par) continue;
		dfs(v, u);
		subsize[u] += subsize[v];
	}
	treesize=subsize[u];
}

int centroid(int u, int par)
{
	int best = 0;
	for(int i = 0; i < adj[u].size(); i++)
	{
		int v = adj[u][i];
		if(visited[v]) continue;
		if(v == par) continue;
		best=max(best,subsize[v]);
	}
	for(int i = 0; i < adj[u].size(); i++)
	{
		int v = adj[u][i];
		if(visited[v]) continue;
		if(v == par) continue;
		int maxsize = treesize-subsize[v];
		for(int j = 0; j < adj[v].size(); j++)
		{
			int w = adj[v][j];
			if(w==u) continue;
			if(visited[w]) continue;
			maxsize=max(maxsize,subsize[w]);
		}
		//if(subsize[v]>treesize-subsize[v]) return centroid(v, u);
		if(maxsize<best) return centroid(v,u);
	}
	return u;
}

int compsolve(int u)
{
	dfs(u, -1);
	int cent = centroid(u, -1);
	//cerr<<u<<' '<<cent<<'\n';
	visited[cent]=1;
	int ans = -1;
	for(int i = 0; i < adj[cent].size(); i++)
	{
		int v = adj[cent][i];
		if(visited[v]) continue;
		ans=max(ans,compsolve(v));
	}
	return ans+1;
}

