#define mp make_pair

const int N = 100100;
const int K = 20;

vector<int> g[N];
vector<pii> dp[N];
vector<pii> a[K];
int n;

void dfs(int v, int par)
{
    int d = 0;
    for (int u : g[v])
    {
        if (u == par) continue;
        dfs(u, v);
        d = max(d, dp[u][0].second);
    }
    for (int i = 0; i < K; i++)
        a[i].clear();
    for (int u : g[v])
        if (u != par)
            a[dp[u][0].second].push_back(mp(u, 0));
    int oldd = d;
    bool ok = true;
//    printf("solving %d\n", v);
    while(true)
    {
//        printf("d = %d\n", d);
        if (d < 0)
        {
            ok = false;
            break;
        }
        if (a[d].empty())
        {
            while(d > 0 && a[d - 1].empty()) d--;
            dp[v].push_back(mp(v, d));
            break;
        }
        if ((int)a[d].size() >= 2)
        {
            ok = false;
            break;
        }
        int u = a[d][0].first;
        int p = a[d][0].second;
//        printf("%d %d\n", u, p);
        dp[v].push_back(dp[u][p]);
        p++;
        if (p < (int)dp[u].size())
            a[dp[u][p].second].push_back(mp(u, p));
        d--;
    }
    if (!ok)
    {
  //      printf("! OK %d\n", v);
        dp[v].clear();
        dp[v].push_back(mp(v, oldd + 1));
    }
    return;
}

