    {
        int v, u;
        scanf("%d%d", &v, &u);
        v--;u--;
        g[v].push_back(u);
        g[u].push_back(v);
    }
    dfs(0, -1);
    printf("%d\n", dp[0][0].second);

    return 0;
}

