void dfs(int v, int p = 0) {
	sz[v] = 1;
	mrk[v] = 1;
	for (auto u: ad[v]) {
		if (u != p && !gone[u]) {
			dfs(u, v);
			sz[v] += sz[u];
		}
	}
}
pair <int, int> get(int v, int p, int sall) {
	pair <int, int> res;
	res.first = v;
	res.second = sall - sz[v];
	for (auto u: ad[v])
	   	if (!gone[u] && u != p)	
			res.second = max(res.second, sz[u]);
	for (auto u: ad[v]) {
		if (u != p && !gone[u]) {
			pair <int, int> t = get(u, v, sall);
			if (t.second < res.second)
				res = t;
		}
	}
	return res;
}
pair <int, int> ffs(int v, int p) {
	pair <int, int> mx = {v, 1};
	for (auto u: ad[v])
		if (u != p) {
			auto t = ffs(u, v);
			t.second++;
			if (t.second > mx.second)
				mx = t;
		}
	return mx;
}
