const int Maxn = 1e5 + 9;
struct edge {
	int to, nxt;
}g[Maxn << 1];
int n, head[Maxn];
void add(int u, int v) { 
	static int e = 0;
	g[++e] = (edge){v, head[u]}, head[u] = e; 
}

int ans = 0;
static int bit[Maxn];

void dfs(int u, int pa) {
	int same = 0;
	for (int i = head[u]; ~i; i = g[i].nxt) {
		int v = g[i].to;
		if (v != pa) {
			dfs(v, u);
			same |= bit[u] & bit[v];
			bit[u] |= bit[v];
		}
	}
	int mx = same ? 32 - __builtin_clz(same) : 0;
	while (bit[u] & (1 << mx)) ++mx;
	bit[u] |= (1 << mx);
	chkmax(ans, mx), bit[u] = bit[u] >> mx << mx;
}

