bool used[110][110000];

void dfs(int v, int p,int dep) {
    used[dep][v] = false;
    for (int i = 0; i < G[v].size(); i++) {
        int cur = G[v][i];
        if (centroid[cur] || cur == p)continue;
        dfs(cur, v, dep);
    }
}

int size_of_subtree(int v, int p,int dep) {
    int ret = 1;
    for (int i = 0; i < G[v].size(); i++) {
        int cur = G[v][i];
        if (centroid[cur] || used[dep][cur] || cur == p)continue;
        ret += size_of_subtree(cur, v, dep);
    }
    return sos[v] = ret;
}

int search_centroid(int v, int t,int dep) {
    if (used[dep][v])return -1;
    int p = -1;
    while (true) {
        int ch, s = 0;
        for (int i = 0; i < G[v].size(); i++) {
            int cur = G[v][i];
            if (centroid[cur] || used[dep][cur] || cur == p)continue;
            if (s <= sos[cur])s = sos[cur], ch = cur;
        }
        if (s <= t / 2)break;
        p = v;
        v = ch;
    }
    return v;
}

int u(int v, int dep) {
    dfs(v, -1, dep);
    int ret = LLONG_MAX;
    while (true) {
        size_of_subtree(v, -1, dep);
        int cidx = search_centroid(v, sos[v], dep);
        if (cidx == -1)return ret;
        centroid[cidx] = true;
        used[dep][cidx] = true;
        int prev=v;
        int mu = -1;
        for (int i = 0; i < G[cidx].size(); i++) {
            if (centroid[G[cidx][i]])continue;
            int cu = u(G[cidx][i], dep + 1);

            if (mu <= cu)mu = cu, v = G[cidx][i];
        }
        ret = min(ret, mu + 1);
        centroid[cidx] = false;
        if(prev==v)return ret;
    }
}

