			dfs(u, v);
			sz[v] += sz[u];
		}
	}
}
pair <int, int> get(int v, int p, int sall) {
	pair <int, int> res;
	res.first = v;
	res.second = sall - sz[v];
	for (auto u: ad[v])
	   	if (!gone[u] && u != p)	
			res.second = max(res.second, sz[u]);
	for (auto u: ad[v]) {
		if (u != p && !gone[u]) {
			pair <int, int> t = get(u, v, sall);
			if (t.second < res.second)
				res = t;
		}
	}
	return res;
}
pair <int, int> ffs(int v, int p) {
	pair <int, int> mx = {v, 1};
	for (auto u: ad[v])
		if (u != p) {
			auto t = ffs(u, v);
			t.second++;
			if (t.second > mx.second)
				mx = t;
		}
	return mx;
}
int main() {
	cin >> n;
	for (int i = 1; i < n; i++) {
		int v, u;
		cin >> v >> u;
		ad[v].push_back(u);
		ad[u].push_back(v);
	}
	int res = 0;
	while (1) {
		bool was = 0;
		memset(sz, 0, sizeof sz);
		memset(mrk, 0, sizeof mrk);
		for (int i = 1; i <= n; i++) 
			if (!gone[i] && !mrk[i]) {
				was = 1;
				dfs(i);
				int v = get(i, 0, sz[i]).first;
				gone[v] = 1;
			}
		if (!was)
			break;
		res++;
	}
	int v = ffs(1, 0).first;
	int d = ffs(v, 0).second;
	int r2 = ((int)log2(d));
	cout << (r2 + res - 1) / 2;

	return 0;
}

