			bool change = false;

			for (int i = 0; i < n; i++)
				if (!del[i] && !Mark[i]) {
					change = true;

					dfs1(i, i);
					dfs2(i, i, 0);
					findS(i, i);
					del[S] = true;

					for (int j = 0; j < G[S].size(); j++)
						if (G[S][j].second) {
							int v = G[S][j].first;
							int ind = Map[{S, v}];

							d[v]--;
							G[v][ind].second = false;
						}
				}

			for (int i = 0; i < n; i++)
				if (!d[i])
					del[i] = true;

			if (!change)
				break;
		}

		ANS = min(ANS, ans - 1);
	}

	cout << ANS << "\n";
	return 0;
}

