typedef long long i64; typedef vector<int> ivec; typedef vector<string> svec;

int N;
vector<int> graph[101010];
int ids[101010];

void remove_leaves()
{
	vector<pair<int, int> > rem;
	for (int i = 0; i < N; ++i) {
		ids[i] = -1;
		for (int j : graph[i]) {
			if (i < j && graph[i].size() > 1 && graph[j].size() > 1) {
				rem.push_back({ i, j });
			}
		}
	}
	for (int i = 0; i < N; ++i) graph[i].clear();

	int idx = 0;
	for (auto e : rem) {
		int u = e.first, v = e.second;
		if (ids[u] == -1) {
			ids[u] = idx++;
		}
		if (ids[v] == -1) {
			ids[v] = idx++;
		}
		graph[ids[u]].push_back(ids[v]);
		graph[ids[v]].push_back(ids[u]);
	}
	N = idx;
}
void visit(int p, int rt, int vrt, int el, int &idx, vector<pair<int, int> > &edges)
{
	if (graph[p].size() == 2) {
		visit(graph[p][0] + graph[p][1] - rt, p, vrt, el + 1, idx, edges);
		return;
	}
	int my = idx++;
	vector<int> bridge;
	bridge.push_back(my);
	for (int i = 0; i < el / 2 - 1; ++i) {
		bridge.push_back(idx++);
	}
	bridge.push_back(vrt);
	for (int i = 0; i < bridge.size() - 1; ++i) {
		edges.push_back({ bridge[i], bridge[i + 1] });
	}
	for (int q : graph[p]) if (q != rt) {
		visit(q, p, my, 1, idx, edges);
	}
}
void contract()
{
	int idx = 0;
	vector<pair<int, int> > edges;
	for (int i = 0; i < N; ++i) if (graph[i].size() != 2) {
		visit(i, -1, -1, 0, idx, edges);
		break;
	}
	for (int i = 0; i < N; ++i) graph[i].clear();
	for (auto e : edges) {
		int u = e.first, v = e.second;
		graph[u].push_back(v);
		graph[v].push_back(u);
	}
	N = idx;
}

int main()
{
	scanf("%d", &N);
	for (int i = 0; i < N - 1; ++i) {
		int x, y;
		scanf("%d%d", &x, &y);
		--x; --y;
		graph[x].push_back(y);
		graph[y].push_back(x);
	}

	int ans = 0;
	while (N > 1) {
		ans += 1;

		remove_leaves();
		contract();
	}
	printf("%d\n", ans);
	return 0;
}

