vector<int> get() {
	vector<int> ans;
	if (ss.empty()) {
		ans.push_back(0);
		return ans;
	}
	if (ss.size() >= 2) {
		auto it = ss.end();
		--it;
		auto it2 = prev(it);
		if (get<0>(*it) == get<0>(*it2)) {
			ans.push_back(get<0>(*it) + 1);
			return ans;
		}
	}
	int k, x, y;
	tie(k, x, y) = *ss.rbegin();
	ss.erase(make_tuple(k, x, y));
	++y;
	if (dp[x][y] != -1)
		ss.insert(make_tuple(dp[x][y], x, y));
	ans = get();
	if (ans.back() < k) {
		ans.push_back(k);
		return ans;
	}
	else {
		ans.clear();
		ans.push_back(k + 1);
		return ans;
	}
}

void dfs1(int v) {
	was[v] = 1;
	vector<int> vv;
	for (int u: eds[v]) {
		if (was[u])
			continue;
		vv.push_back(u);
		dfs1(u);
	}
	if (vv.empty()) {
		dp[v][0] = 0;
		for (int i = 1; i < LOG; ++i)
			dp[v][i] = -1;
		return;
	}
	ss.clear();
	for (int i: vv)
		ss.insert(make_tuple(dp[i][0], i, 0));
	vector<int> tmp = get();
	reverse(tmp.begin(), tmp.end());
	for (int i = 0; i < tmp.size(); ++i)
		dp[v][i] = tmp[i];
	for (int i = tmp.size(); i < LOG; ++i)
		dp[v][i] = -1;
}

