  private static int dfs(int now, boolean[] ved, int[][] g) {

    int[] dia = diameter(g, now, ved);
    ved[dia[3]] = true;
    int max = -1;

    for (int next : g[dia[3]]) {
      if (ved[next]) continue;
      max = Math.max(max, dfs(next, ved, g));
    }

    return max + 1;
  }

  public static int[][] packU(int n, int[] from, int[] to){ return packU(n, from, to, from.length); }
  public static int[][] packU(int n, int[] from, int[] to, int sup)
  {
      int[][] g = new int[n][];
      int[] p = new int[n];
      for(int i = 0;i < sup;i++)p[from[i]]++;
      for(int i = 0;i < sup;i++)p[to[i]]++;
      for(int i = 0;i < n;i++)g[i] = new int[p[i]];
      for(int i = 0;i < sup;i++){
          g[from[i]][--p[from[i]]] = to[i];
          g[to[i]][--p[to[i]]] = from[i];
      }
      return g;
  }


  public static int[] diameter(int[][] g, int v, boolean[] sved)
  {
      int n = g.length;
      int f0 = -1, f1 = -1, d01 = -1, c = -1;
      int[] q = new int[n];
      boolean[] ved = new boolean[n];
      {
          int qp = 0;
          q[qp++] = v; ved[0] = true;
          for(int i = 0;i < qp;i++){
              int cur = q[i];
              for(int e : g[cur]){
                  if(!ved[e] && !sved[e]){
                      ved[e] = true;
                      q[qp++] = e;
                      continue;
                  }
              }
          }
          f0 = q[qp-1];
      }
      {
          int[] d = new int[n];
          int qp = 0;
          Arrays.fill(ved, false);

          q[qp++] = f0; ved[f0] = true;
          for(int i = 0;i < qp;i++){
              int cur = q[i];
              for(int e : g[cur]){
                  if(!ved[e] && !sved[e]){
                      ved[e] = true;
                      q[qp++] = e;
                      d[e] = d[cur] + 1;
                      continue;
                  }
              }
          }
          f1 = q[qp-1];
          c = q[qp / 2];
          d01 = d[f1];
      }
      return new int[]{d01, f0, f1, c};
  }
