import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.ArrayDeque;
import java.util.Arrays;
import java.util.InputMismatchException;
import java.util.Queue;

public class Main {
	static InputStream is;
	static PrintWriter out;
	static String INPUT = "";

	static void solve()
	{
		int n = ni();
		int[] from = new int[n-1];
		int[] to = new int[n-1];
		for(int i = 0;i < n-1;i++){
			from[i] = ni()-1;
			to[i] = ni()-1;
		}
		int[][] g = packU(n, from, to);
		out.println(go(g));
	}

	static int go(int[][] g)
	{
		if(g.length == 1)return 0;
		int n= g.length;
		int[] cent = center(g);
		int[] map = new int[n];
		Arrays.fill(map, -1);
		int[] lfrom = new int[n];
		int[] lto = new int[n];
		int max = 0;
		for(int e : g[cent[0]]){
			Queue<Integer> q = new ArrayDeque<>();
			q.add(e);
			int id = 0;
			map[e] = id++;
			int u = 0;
			while(!q.isEmpty()){
				int cur = q.poll();
				for(int f : g[cur]){
					if(f != cent[0] && map[f] == -1){
						map[f] = id++;
						lfrom[u] = map[cur]; lto[u] = map[f]; u++;
						q.add(f);
					}
				}
			}
			int[][] lg = packU(id, lfrom, lto, u);
			max = Math.max(max, go(lg));
		}
		return max + 1;
	}

	public static int[][] packU(int n, int[] from, int[] to){ return packU(n, from, to, from.length); }
	public static int[][] packU(int n, int[] from, int[] to, int sup)
	{
		int[][] g = new int[n][];
		int[] p = new int[n];
		for(int i = 0;i < sup;i++)p[from[i]]++;
		for(int i = 0;i < sup;i++)p[to[i]]++;
		for(int i = 0;i < n;i++)g[i] = new int[p[i]];
		for(int i = 0;i < sup;i++){
			g[from[i]][--p[from[i]]] = to[i];
			g[to[i]][--p[to[i]]] = from[i];
		}
		return g;
	}


	public static int[] center(int[][] g)
	{
		int n = g.length;
		int start = 0;
		int[] d1 = dist(g, start);
		int maxd1 = -1;
		int argmaxd1 = -1;
		for(int i = 0;i < n;i++){
			if(d1[i] > maxd1){
				maxd1 = d1[i];
				argmaxd1 = i;
			}
		}

		int[] d2 = dist(g, argmaxd1);
		int maxd2 = -1;
		int argmaxd2 = -1;
		for(int i = 0;i < n;i++){
			if(d2[i] > maxd2){
				maxd2 = d2[i];
				argmaxd2 = i;
			}
		}

		if(maxd2 % 2 == 0){
			int h = maxd2/2;
			int j = argmaxd2;
			outer:
			for(int i = maxd2;i > h;i--){
				for(int e : g[j]){
					if(d2[e] == i-1){
						j = e;
						continue outer;
					}
				}
				throw new RuntimeException();
			}
			return new int[]{j};
		}else{
			int h = maxd2/2;
			int j = argmaxd2;
			int[] cs = new int[2];
			int p = 0;
			outer:
			for(int i = maxd2;i >= h;i--){
				if(i <= h+1)cs[p++] = j;
				if(i > h){
					for(int e : g[j]){
						if(d2[e] == i-1){
							j = e;
							continue outer;
						}
					}
					throw new RuntimeException();
				}
			}
			return cs;
		}
	}

	public static int[] dist(int[][] g, int start)
	{
		int n = g.length;
		int[] d = new int[n];
		Arrays.fill(d, n+3);
		int[] q = new int[n];
		int p = 0;
		q[p++] = start;
		d[start] = 0;
		for(int r = 0;r < p;r++){
			int cur = q[r];
			for(int e : g[cur]){
				if(d[e] > d[cur] + 1){
					d[e] = d[cur] + 1;
					q[p++] = e;
				}
			}
		}
		return d;
	}


	public static int[][] parents3(int[][] g, int root) {
		int n = g.length;
		int[] par = new int[n];
		Arrays.fill(par, -1);

		int[] depth = new int[n];
		depth[0] = 0;

		int[] q = new int[n];
		q[0] = root;
		for (int p = 0, r = 1; p < r; p++) {
			int cur = q[p];
			for (int nex : g[cur]) {
				if (par[cur] != nex) {
					q[r++] = nex;
					par[nex] = cur;
					depth[nex] = depth[cur] + 1;
				}
			}
		}
		return new int[][] { par, q, depth };
	}


	public static int[][] parentToG(int[] par)
	{
		int n = par.length;
		int[] ct = new int[n];
		for(int i = 0;i < n;i++){
			if(par[i] >= 0){
				ct[i]++;
				ct[par[i]]++;
			}
		}
		int[][] g = new int[n][];
		for(int i = 0;i < n;i++){
			g[i] = new int[ct[i]];
		}
		for(int i = 0;i < n;i++){
			if(par[i] >= 0){
				g[par[i]][--ct[par[i]]] = i;
				g[i][--ct[i]] = par[i];
			}
		}
		return g;
	}


	public static int[] buildCentroidTree(int[][] g) {
		int n = g.length;
		int[] ctpar = new int[n];
		Arrays.fill(ctpar, -1);
		buildCentroidTree(g, 0, new boolean[n], new int[n], new int[n], new int[n], ctpar);
		return ctpar;
	}

	private static int buildCentroidTree(int[][] g, int root, boolean[] sed, int[] par, int[] ord, int[] des, int[] ctpar)
	{
		// parent and level-order
		ord[0] = root;
		par[root] = -1;
		int r = 1;
		for(int p = 0;p < r;p++) {
			int cur = ord[p];
			for(int nex : g[cur]){
				if(par[cur] != nex && !sed[nex]){
					ord[r++] = nex;
					par[nex] = cur;
				}
			}
		}
		// if(r == 1)return;

		// DP and find a separator
		int sep = -1; // always exists
		outer:
		for(int i = r-1;i >= 0;i--){
			int cur = ord[i];
			des[cur] = 1;
			for(int e : g[cur]){
				if(par[cur] != e && !sed[e])des[cur] += des[e];
			}
			if(r-des[cur] <= r/2){
				for(int e : g[cur]){
					if(par[cur] != e && !sed[e] && des[e] >= r/2+1)continue outer;
				}
				sep = cur;
				break;
			}
		}

		sed[sep] = true;
		for(int e : g[sep]){
			if(!sed[e])ctpar[buildCentroidTree(g, e, sed, par, ord, des, ctpar)] = sep;
		}
		return sep;
	}
