		int[] to = new int[n-1];
		for(int i = 0;i < n-1;i++){
			from[i] = ni()-1;
			to[i] = ni()-1;
		}
		int[][] g = packU(n, from, to);
		int[] ctpar = buildCentroidTree(g);
		int[][] cg = parentToG(ctpar);
		int root = -1;
		for(int i = 0;i < n;i++)if(ctpar[i] == -1)root = i;
