		long[] dp = new long[n];
		for(int i = n-1;i >= 0;i--){
			int cur = ord[i];
			long once = 0;
			long twice = 0;
			for(int e : g[cur]){
				if(par[cur] == e)continue;
				dp[cur] |= dp[e];
				twice |= once&dp[e];
				once |= dp[e];
