void main() {
    auto sc = new Scanner(stdin);
    int n;
    sc.read(n);
    int[][] g = new int[][](n);
    foreach(i; 0..n-1) {
        int a, b;
        sc.read(a, b); a--; b--;
        g[a] ~= b; g[b] ~= a;
    }
    long dfs(int p, int b) {
        long one, two;
        foreach (d; g[p]) {
            if (d == b) continue;
            long x = dfs(d, p);
            two |= one & x;
            one |= x;
        }
        int off = two ? two.bsr + 1 : 0;
        one = ((one >> off) + 1) << off;
        return one;
    }

    writeln(dfs(0, -1).bsr);
}
