const int maxn = (int) 1e5 + 10;
pair<int, int> tosort[maxn];

int n;

int read() {
  if (scanf("%d", &n) < 1) {
    return 0;
  }
  for (int i = 0; i < n; ++i) {
    int x, y;
    scanf("%d%d", &x, &y);
    tosort[i] = mp(x, y);
  }
  return 1;
}

void solve() {
  if (tosort[0].first || tosort[1].second) {
    printf("-1\n");
    return;
  }
  map<pair<int, int>, int> cnt;
  for (int i = 0; i < n; ++i) {
    int x = tosort[i].first;
    int y = tosort[i].second;
    if (i != 0 && !x) {
      printf("-1\n");
      return;
    }
    if (i != 1 && !y) {
      printf("-1\n");
      return;
    }
    cnt[{x, y}] += 1;
    tosort[i] = {x + y, x};
  }
  sort(tosort, tosort + n);

  int res = 2 * n - 2;
  for (int i = 0; i < n;) {
    int i0 = i;
    while (i < n && tosort[i].first == tosort[i0].first) {
      int x = tosort[i].first, y = tosort[i].second;
      x -= y;

      for (int iter = 0; iter < 2; ++iter) {
        auto &cur = (!iter ? x : y);
        if (!cur) {
          continue;
        }
        auto &cury = (iter ? x : y);
        --cur;
        bool ok = 0;
        for (int dx = -1; dx <= 1; ++dx) {
          cury += dx;
          if (cnt.count({x, y})) {
            ok = 1;
          }
          cury -= dx;
        }
        ++cur;
        if (!ok) {
          printf("-1\n");
          return;
        }
      }
      ++i;
    }

    int last = 0, left = 0;
    for (int j = i0; j < i;) {
      int j0 = j;
      while (j < i && tosort[j].second == tosort[j0].second) {
        ++j;
      }
      int now = j - j0;

      int del = 0;
      if (last + 1 == tosort[j0].second) {
        del = min(now, left);
        res -= del;
      }
      if (cnt.count({tosort[j0].first - tosort[j0].second - 1, tosort[j0].second - 1})) {
        res -= now - del;
        now = del;
      }
      left = now;
      last = tosort[j0].second;
    }
  }
  printf("%d\n", res);
}

int main() {
  precalc();
#ifdef LOCAL
  freopen(TASK ".out", "w", stdout);
  assert(freopen(TASK ".in", "r", stdin));
#endif

  while (1) {
    if (!read()) {
      break;
    }
    solve();
#ifdef DEBUG
    eprintf("Time %.2f\n", (double) clock() / CLOCKS_PER_SEC);
#endif
  }
  return 0;
}

