int N, A[101010], B[101010];

void fail()
{
	puts("-1");
	exit(0);
}

int bas[101010];
set<pair<int, int> > P;

int main()
{
	scanf("%d", &N);
	for (int i = 0; i < N; ++i) scanf("%d%d", A + i, B + i);

	if (A[0] != 0) fail();
	if (B[1] != 0) fail();
	if (A[1] != B[0]) fail();
	int D = A[1];
//	puts("x");
	for (int i = 2; i < N; ++i) {
		if (A[i] == 0 || B[i] == 0 || A[i] + B[i] < D) fail();
	}

	for (int i = 0; i < N; ++i) {
		if (A[i] + B[i] == D) {
			bas[A[i]] += 1;
		}
	}
//	puts("y");

	int ret = 0;
	for (int i = 0; i < D; ++i) {
		if (bas[i] == 0) fail();
		ret += max(bas[i], bas[i + 1]);
	}
//	puts("z");

	for (int i = 0; i < N; ++i) P.insert(make_pair(A[i], B[i]));
	for (int i = 2; i < N; ++i) if (A[i] + B[i] != D) {
		int x = A[i], y = B[i];
		if (P.count(make_pair(x - 1, y - 1))) {
			++ret;
		} else if (P.count(make_pair(x, y - 1)) || P.count(make_pair(x - 1, y))) {
			ret += 2;
		} else fail();
	}
	printf("%d\n", ret);

	return 0;
}

#include<cstdio>
#include<algorithm>
