#define pii pair<int,int>
int n, chk[101000], cnt, m;
using namespace std;
struct point {
	int x, y, num;
	bool operator<(const point &p)const {
		return x + y != p.x + p.y ? x+y < p.x+p.y : x<p.x;
	}
}P[101000], U[101000];
int A[101000], B[101000], res, D[101000][2], first = 1;
vector<int>G[101000];
map<pii, int>Map;
int Get(int x, int y) {
	if (Map.count({ x,y }))return Map[{x, y}];
	return 0;
}
bool Go() {
	int i;
	if (first) {
		first = 0;
		for (i = 1; i < m; i++) {
			int x = U[i].x, y = U[i].y;
			res += max(Get(x, y), Get(x + 1, y - 1));
		}
		return true;
	}
	for (i = 0; i <= m + 1; i++) {
		D[i][0] = D[i][1] = 1e9;
	}
	D[0][0] = 0;
	for (i = 1; i <= m; i++) {
		int x = U[i].x, y = U[i].y;
		int ck1 = 0, ck2 = 0, ck3 = 0;
		if (Get(x-1,y-1)) ck1 = 1;
		if (Get(x-1,y)) ck2 = 1;
		if (Get(x,y-1)) ck3 = 1;
		if (ck1) {
			D[i][0] = min(D[i][0], D[i - 1][0] + Get(x, y));
		}
		if (ck2) {
			D[i][1] = min(D[i][1], D[i - 1][0] + Get(x, y));
		}
		if (ck2 &&ck3) {
			D[i][0] = min(D[i][0], D[i - 1][0] + Get(x, y) * 2);
		}
		if (i != 1) {
			D[i][1] = min(D[i][1], min(D[i - 1][0], D[i - 1][1]) + max(Get(x - 1, y + 1), Get(x, y)));
		}
		if (ck1 || ck3) {
			D[i][0] = min(D[i][0], D[i][1] + Get(x, y));
		}
	}
	if (D[m][0] > 1e8)return false;
	res += D[m][0];
	return true;
}
int main() {
	int i, j;
	scanf("%d", &n);
	for (i = 1; i <= n; i++) {
		scanf("%d%d", &A[i], &B[i]);
		P[i] = { A[i],B[i],i };
		Map[{A[i], B[i]}]++;
	}
	sort(P + 1, P + n + 1);
	if (A[1] != 0 || B[2] != 0 || A[2]!=B[1] || Get(A[1],B[1])!=1 || Get(A[2],B[2])!=1) {
		puts("-1");
		return 0;
	}
	for (i = 1; i <= n; i++) {
		if (abs(A[i] - B[i]) > A[2]) {
			puts("-1");
			return 0;
		}
		G[Get(A[i], B[i])].push_back(i);
	}
	for (i = 1; i <= n; i++) {
		m = 0;
		U[++m] = P[i];
		for (j = i+1; j <= n; j++) {
			if (abs(P[j].x - P[j - 1].x) > 1 || P[j].x + P[j].y != P[j - 1].x + P[j - 1].y)break;
			if (P[j].x != P[j - 1].x)U[++m] = P[j];
		}
		if (i == 1) {
			if (U[1].num != 1 || U[m].num != 2) {
				puts("-1");
				return 0;
			}
		}
		if (!Go()) {
			puts("-1");
			return 0;
		}
		i = j - 1;
	}
	printf("%d\n", res);
}

