void ng() {
        printf("-1\n");
        exit(0);
}

int main() {
        int n;
        scanf("%d", &n);
        set<pair<int, int>> ps;
        vector<pair<int, int>> p;
        for (int i = 0; i < n; i ++) {
                int a, b;
                scanf("%d%d", &a, &b);
                p.emplace_back(a, b);
                ps.insert({a, b});
        }
        if (p[0].first != 0 || p[1].second != 0) ng();
        if (p[0].second != p[1].first) ng();
        if (p[0].second == 0) ng();
        for (int i = 2; i < n; i ++) {
                int a, b;
                tie(a, b) = p[i];
                if (a == 0 || b == 0) ng();
                if (!ps.count({a - 1, b + 1}) && !ps.count({a - 1, b}) && !ps.count({a - 1, b - 1})) ng();
                if (!ps.count({a + 1, b - 1}) && !ps.count({a, b - 1}) && !ps.count({a - 1, b - 1})) ng();
        }
        map<int, vector<pair<int, int>>> s;
        for (int i = 0; i < n; i ++) {
                s[p[i].first + p[i].second].emplace_back(p[i].first, p[i].second);
        }
        int matching = 0;
        for (auto mp : s) {
                vector<pair<int, int>> v = mp.second;
                sort(v.begin(), v.end());
                int cur = v[0].first;
                vector<pair<bool, pair<int, int>>> t; //(bottom left exists, number of the same position)
                for (int i = 0; i < (int) v.size(); i ++) {
                        int cnt = 0;
                        bool ex = ps.count({v[i].first - 1, v[i].second - 1});
                        while (i < (int) v.size() && cur == v[i].first) {
                                i ++;
                                cnt ++;
                        }
                        i --;
                        t.push_back({ex, {cnt, cnt}});
                        cur ++;
                }
                for (int i = 0; i < (int) t.size(); i ++) {
                        if (t[i].first) {
                                int d = min(t[i].second.first, t[i].second.second);
                                matching += d;
                                t[i].second.first -= d;
                                t[i].second.second -= d;
                        }
                        if (t[i].second.second > 0 && i + 1 < (int) t.size()) {
                                int d = min(t[i].second.second, t[i + 1].second.first);
                                matching += d;
                                t[i + 1].second.first -= d;
                        }
                }
        }
        printf("%d\n", 2 * n - 2 - matching);
        return 0;
}

#include<bits/stdc++.h>

using namespace std;

int nr, N, a[100009], b[100009], A[100009], B[100009], id[100009], sz[100009], may[100009], both1[100009], dp[100009][2];
const int INF = 1e8;

bool cmp (int i, int j)
{
    if (A[i] + B[i] == A[j] + B[j]) return (A[i] < A[j]);
    return (A[i] + B[i] < A[j] + B[j]);
}

int findPair (int x, int y)
{
    int p = 1, u = nr, mij;
    while (p <= u)
    {
        mij = (p + u) >> 1;
        if (a[mij] == x && b[mij] == y) return mij;
        if (a[mij] + b[mij] < x + y || (a[mij] + b[mij] == x + y && a[mij] < x)) p = mij + 1;
        else u = mij - 1;
    }
    return 0;
}

int main ()
{
//freopen ("input", "r", stdin);
//freopen ("output", "w", stdout);

scanf ("%d", &N);
for (int i=1; i<=N; i++)
    scanf ("%d %d", &A[i], &B[i]), id[i] = i;
sort (id + 1, id + N + 1, cmp);
if (A[2] != B[1] || A[id[1]] + B[id[1]] < A[2] || A[1] != 0 || B[2] != 0)
{
    printf ("-1\n");
    return 0;
}
for (int i=1; i<=N; i++)
    if ((A[i] == 0 && i != 1) || (B[i] == 0 && i != 2))
    {
        printf ("-1\n");
        return 0;
    }
for (int i=1; i<=N; i++)
{
    int j = i;
    while (j + 1 <= N && A[id[j]] == A[id[j + 1]] && B[id[j]] == B[id[j + 1]])
        j ++;
    a[++nr] = A[id[i]], b[nr] = B[id[i]], sz[nr] = j - i + 1;
    i = j;
}
for (int i=1; i<=nr; i++)
    may[i] = (findPair (a[i] + 1, b[i] - 1) != 0),
    both1[i] = (findPair (a[i] - 1, b[i] - 1) != 0);
if (!may[1])
{
    printf ("-1\n");
    return 0;
}
for (int i=1; i<=nr; i++)
    if ((a[i] > 0 && findPair (a[i] - 1, b[i]) == 0 && findPair (a[i] - 1, b[i] + 1) == 0 && both1[i] == 0) ||
        (b[i] > 0 && findPair (a[i], b[i] - 1) == 0 && findPair (a[i] + 1, b[i] - 1) == 0 && both1[i] == 0))
        {
            printf ("-1\n");
            return 0;
        }
for (int i=1; i<=nr; i++)
    for (int j=0; j<2; j++)
        dp[i][j] = INF;
dp[1][1] = max (1, sz[2]);
for (int i=1; i<nr; i++)
    for (int j=0; j<=may[i]; j++)
        for (int k=0; k<=may[i + 1]; k++)
        {
            if (k == 0 && both1[i + 1] == 1)
                dp[i + 1][k] = min (dp[i + 1][k], dp[i][j] + sz[i + 1]);
            int curr = dp[i][j];
            if (j == 0)
            {
                if (findPair (a[i + 1] - 1, b[i + 1]) == 0 && both1[i + 1] == 0) continue;
                curr += sz[i + 1];
            }
            if (k == 0 && b[i + 1] > 0)
                curr += sz[i + 1];
            else
            if (k == 1)
            {
                if (sz[i + 1] <= sz[i + 2] || both1[i + 1] == 0) curr += max (sz[i + 1], sz[i + 2]);
                else curr += sz[i + 2] + sz[i + 1] * j;
            }
            if (curr < dp[i + 1][k])
                dp[i + 1][k] = curr;
        }
printf ("%d\n", dp[nr][0]);
return 0;
}

