					ret += V[i].second;
					V[i].second = 0;
				}
			}
			for (int i = 1; i < V.size(); ++i) {
				ret += max(V[i].second, V[i - 1].second);
			}
		}
	}
	printf("%d\n", ret);

	return 0;
}

