using namespace std;

const int N = 1e5+7;

int n, ans;

int A[N];
int B[N];

PII mB[N]; //maksymalne B dla danego A
PII mA[N]; //maksymalne A dla danego B

map<PII, int> takie;

int32_t main()
{
	ios_base::sync_with_stdio(0);
	cin.tie(NULL);

	cin >> n;

	for(int i = 1; i <= n; ++i)
	{
		cin >> A[i] >> B[i];
		takie[{A[i], B[i]}] = i;
		mB[A[i]] = max(mB[A[i]], mp(B[i], i));
		mA[B[i]] = max(mA[B[i]], mp(A[i], i));
	}

	if(A[2]!=B[1] || A[1] || B[2])
	{
		cout << -1 << endl;
		return 0;
	}

	if(A[2]==1)
		++ans;

	for(int i = 3; i <= n; ++i)
	{
		if(takie.find({A[i]-1, B[i]-1})!=takie.end())
			++ans;
		else
		{
			if(mB[A[i]-1].st>B[i]-1 && mA[B[i]-1].st>A[i]-1)
				ans += 2;
			else
			{
				cout << -1 << endl;
				return 0;
			}
		}
	}

	cout << ans << endl;
}

#include<bits/stdc++.h>
using namespace std;
const int maxn=100005;
struct SHIT
{
    int one,two;
    bool operator<(const SHIT  &b)const {
        if(this->one!=b.one) return (this->one<b.one);
        return (this->two<b.two);
    }
}shit[maxn];
bool cmp(SHIT a,SHIT b)
{
    if(a.one+a.two!=b.one+b.two)
    return a.one+a.two<b.one+b.two;
    return a.one<b.one;
}
map<SHIT,bool>mp;
int main()
{
    int n;
    scanf("%d",&n);
    for(int i=1;i<=n;i++)
    {
        scanf("%d%d",&shit[i].one,&shit[i].two);
    }
    if(!(shit[1].one==shit[2].two&&shit[1].one==0&&shit[1].two!=0)){
        puts("-1");
        return 0;
    }
    int mn=shit[1].two;
    int ans=2*n-2;
    if(n==2){
        if(mn==1){
            puts("1");
        }
        else{
            puts("-1");
        }
        return 0;
    }
    for(int i=3;i<=n;i++)
    {
        if(shit[i].one==0||shit[i].two==0||shit[i].one+shit[i].two<mn){
            puts("-1");
            return 0;
        }
    }
    sort(shit+1,shit+n+1,cmp);
    mp.clear();
    for(int i=1,j;i<=n;i=j)
    {
        j=i+1;
        while(j<=n&&shit[j].one+shit[j].two==shit[i].one+shit[i].two)
            j++;
        for(int k=i;k<j;k++)
            mp[shit[k]]=1;
        int last=0,left=0;
        for(int k=i,tk;k<j;k=tk)
        {
            tk=k+1;
            while(tk<j&&shit[tk].one==shit[k].one&&shit[tk].two==shit[k].two){
                tk++;
            }
            int now=tk-k;
            SHIT tmp1,tmp2;
            tmp1.one=shit[k].one-1;
            tmp1.two=shit[k].two-1;
            if(mp.count(tmp1)){
                ans-=now;
                last=shit[i].one;
                left=now;
                continue;
            }


            tmp1.one=shit[k].one-1;
            tmp1.two=shit[k].two+1;
            tmp2.one=shit[k].one+1;
            tmp2.two=shit[k].two-1;

            bool one=0,two=0;

            one=one|mp.count(tmp1);
            two=two|mp.count(tmp2);

            tmp1.two--;
            tmp2.one--;
            one=one|mp.count(tmp1);
            two=two|mp.count(tmp2);

            if((one==0||two==0)&&shit[k].one!=0&&shit[k].two!=0){
                //printf("%d k %d\n",shit[k].one,shit[k].two);
                puts("-1");
                return 0;
            }
            if(last+1==shit[k].one){
                ans-=max(now,left);
            }
            last=shit[i].one;
            left=now;
        }
    }
    printf("%d\n",ans);
    return 0;
}

#include<bits/stdc++.h>

using namespace std;

int nr, N, a[100009], b[100009], A[100009], B[100009], id[100009], sz[100009], may[100009], both1[100009], dp[100009][2];
const int INF = 1e8;

bool cmp (int i, int j)
{
    if (A[i] + B[i] == A[j] + B[j]) return (A[i] < A[j]);
    return (A[i] + B[i] < A[j] + B[j]);
}

int findPair (int x, int y)
{
    int p = 1, u = nr, mij;
    while (p <= u)
    {
        mij = (p + u) >> 1;
        if (a[mij] == x && b[mij] == y) return mij;
        if (a[mij] + b[mij] < x + y || (a[mij] + b[mij] == x + y && a[mij] < x)) p = mij + 1;
        else u = mij - 1;
    }
    return 0;
}

int main ()
{
//freopen ("input", "r", stdin);
//freopen ("output", "w", stdout);

scanf ("%d", &N);
for (int i=1; i<=N; i++)
    scanf ("%d %d", &A[i], &B[i]), id[i] = i;
sort (id + 1, id + N + 1, cmp);
if (A[2] != B[1] || A[id[1]] + B[id[1]] < A[2] || A[1] != 0 || B[2] != 0)
{
    printf ("-1\n");
    return 0;
}
for (int i=1; i<=N; i++)
    if ((A[i] == 0 && i != 1) || (B[i] == 0 && i != 2))
    {
        printf ("-1\n");
        return 0;
    }
for (int i=1; i<=N; i++)
{
    int j = i;
    while (j + 1 <= N && A[id[j]] == A[id[j + 1]] && B[id[j]] == B[id[j + 1]])
        j ++;
    a[++nr] = A[id[i]], b[nr] = B[id[i]], sz[nr] = j - i + 1;
    i = j;
}
for (int i=1; i<=nr; i++)
    may[i] = (findPair (a[i] + 1, b[i] - 1) != 0),
    both1[i] = (findPair (a[i] - 1, b[i] - 1) != 0);
if (!may[1])
{
    printf ("-1\n");
    return 0;
}
for (int i=1; i<=nr; i++)
    if ((a[i] > 0 && findPair (a[i] - 1, b[i]) == 0 && findPair (a[i] - 1, b[i] + 1) == 0 && both1[i] == 0) ||
        (b[i] > 0 && findPair (a[i], b[i] - 1) == 0 && findPair (a[i] + 1, b[i] - 1) == 0 && both1[i] == 0))
        {
            printf ("-1\n");
            return 0;
        }
for (int i=1; i<=nr; i++)
    for (int j=0; j<2; j++)
        dp[i][j] = INF;
dp[1][1] = max (1, sz[2]);
for (int i=1; i<nr; i++)
    for (int j=0; j<=may[i]; j++)
        for (int k=0; k<=may[i + 1]; k++)
        {
            if (k == 0 && both1[i + 1] == 1)
                dp[i + 1][k] = min (dp[i + 1][k], dp[i][j] + sz[i + 1]);
            int curr = dp[i][j];
            if (j == 0)
            {
                if (findPair (a[i + 1] - 1, b[i + 1]) == 0 && both1[i + 1] == 0) continue;
                curr += sz[i + 1];
            }
            if (k == 0 && b[i + 1] > 0)
            {
                if (findPair (a[i + 1], b[i + 1] - 1) == 0 && both1[i + 1] == 0) continue;
                curr += sz[i + 1];
            }
            else
            if (k == 1) curr += max (sz[i + 1], sz[i + 2]);
            if (curr < dp[i + 1][k])
                dp[i + 1][k] = curr;
        }
printf ("%d\n", min (dp[nr][0], dp[nr][1]));
return 0;
}

