	for(int i=1;i<n;i++) for(int j=0;j<a[i].size();j++){
		int x=a[i][j];
		if(x<abs(i-d)||i+d<x) res=-inf;
		if(!x&&!j||f(i-1,x-1)) continue;
		if(f(i+1,x-1)){
			int t=f(i+1,x-1),I=upper_bound(a[i].begin(),a[i].end(),x)-a[i].begin();
			res+=max(I-j-t,0);
			j=I-1;
		}
		else if(f(i,x-1)) res++;
		else res=-inf;
		if(f(i-1,x-1)+f(i-1,x)+f(i-1,x+1)==0) res=-inf;
