        if (cur[i0] != last + 1) {
          printf("-1\n");
          return;
        }
        res += i - i0;
        if (odd && cur[i0] == 1) {
          cool.pb(i - i0);
        }
        ++last;
      }
      if (sz(cool) == i) {
        cool.pb(0);
      }
    }
    if (odd) {
      for (int i = 0; i < sz(cool); ++i) {
        while (cool[i]) {
          int j = i;
          while (j < sz(cool) && cool[j]) {
            --cool[j];
            ++j;
          }
          ++res;
        }
      }
    }
  }
  if (big[0] != 1 || big[d] != 1) {
    printf("-1\n");
    return;
  }
  for (int i = 1; i < d; ++i) {
    if (big[i] == 0) {
      printf("-1\n");
      return;
    }
  }
  for (int i = 0; i < d; ++i) {
    res += max(big[i], big[i + 1]);
  }
  if (0) {
  printf("%d\n", res);
  }
}

