PII mA[N]; //maksymalne A dla danego B

map<PII, int> takie;

VI G[N];

queue<int> Q;

int dist[3][N];

bool check()
{
	for(int i = 1; i <= 2; ++i)
	{
		for(int v = 1; v <= n; ++v)
		{
			dist[i][v] = inf;
		}

		dist[i][i] = 0;
		Q.push(i);

		while(!Q.empty())
		{
			int v = Q.front();
			Q.pop();

			for(auto it:G[v])
			{
				if(dist[i][it]>dist[i][v]+1)
				{
					dist[i][it] = dist[i][v]+1;
					Q.push(it);
				}
			}
		}
	}

	for(int i = 1; i <= n; ++i)
	{
		if(A[i]!=dist[1][i])
			return 0;

		if(B[i]!=dist[2][i])
			return 0;
	}

	return 1;
}

