{
	ios_base::sync_with_stdio(0);
	cin.tie(NULL);

	cin >> n;

	for(int i = 1; i <= n; ++i)
	{
		cin >> A[i] >> B[i];
		takie[{A[i], B[i]}] = i;
		mB[A[i]] = max(mB[A[i]], mp(B[i], i));
		mA[B[i]] = max(mA[B[i]], mp(A[i], i));
	}

	if(A[2]!=B[1] || A[1] || B[2])
	{
		cout << -1 << endl;
		return 0;
	}

	if(A[2]==1)
	{
		G[1].pb(2);
		G[2].pb(1);
	}

	for(int i = 3; i <= n; ++i)
	{
		if(takie.find({A[i]-1, B[i]-1})!=takie.end())
		{
			G[i].pb(takie[{A[i]-1, B[i]-1}]);
			G[takie[{A[i]-1, B[i]-1}]].pb(i);
		}
		else
		{
			if(mB[A[i]-1].st>B[i]-1 && mA[B[i]-1].st>A[i]-1)
