    if (left < 0 || abs(ds[0][v] - ds[1][v]) > d) {
      printf("-1\n");
      return;
    }
    if (left > 0) {
      vs[left & 1][(ds[0][v] - ds[1][v] + d) / 2].pb((left + 1) / 2);
    } else {
      big[ds[0][v]] += 1;
    }
  }
  res = 0;
  for (int odd = 0; odd < 2; ++odd) {
    vector<int> cool;
    for (int i = 0; i <= d - odd; ++i) {
      auto &cur = vs[odd][i];
      sort(cur.begin(), cur.end());
      int last = 0;
      for (int i = 0; i < sz(cur);) {
        int i0 = i;
        while (i < sz(cur) && cur[i] == cur[i0]) {
          ++i;
        }
        //eprintf("%dx  %d\n", i - i0, cur[i0]);
        if (cur[i0] != last + 1) {
          printf("-1\n");
          return;
        }
        res += i - i0;
        if (odd && cur[i0] == 1) {
          cool.pb(i - i0);
        }
        ++last;
      }
      if (sz(cool) == i) {
        cool.pb(0);
      }
    }
    if (odd) {
      for (int i = 0; i < sz(cool); ++i) {
        while (cool[i]) {
          int j = i;
          while (j < sz(cool) && cool[j]) {
            --cool[j];
            ++j;
          }
          ++res;
        }
      }
    }
  }
  if (big[0] != 1 || big[d] != 1) {
    printf("-1\n");
    return;
  }
  for (int i = 1; i < d; ++i) {
    if (big[i] == 0) {
      printf("-1\n");
      return;
    }
  }
  for (int i = 0; i < d; ++i) {
    res += max(big[i], big[i + 1]);
  }
  if (0) {
  printf("%d\n", res);
  }
}

void gen() {
  vector<vector<int> > es;
  vector<pair<int, int> > edges;

  int m;
  while (1) {
    beg:;
  n = rnd(20) + 2;
