  edges.clear();
  for (int i = 0; i < n; ++i) {
    for (int j = i + 1; j < n; ++j) {
      edges.pb(mp(i, j));
    }
  }
  random_shuffle(edges.begin(), edges.end(), rnd);
  m = rnd(sz(edges) + 1);
  edges.resize(m);
  for (auto cur : edges) {
    int i = cur.first, j = cur.second;
    es[i].pb(j), es[j].pb(i);
  }

  for (int i = 0; i < 2; ++i) {
    auto &dist = ds[i];
    for (int v = 0; v < n; ++v) {
      dist[v] = inf;
    }
    vector<int> st;
    st.pb(i);
    dist[i] = 0;
    for (int l = 0; l < sz(st); ++l) {
      int v = st[l];
      for (int u : es[v]) {
        if (dist[u] > dist[v] + 1) {
          st.pb(u);
          dist[u] = dist[v] + 1;
        }
      }
    }
    if (sz(st) < n) {
      goto beg;
    }
  }
  break;
  }
